    /**
     * Check signature.
     *
     * @param idInterv the id interv
     * @return the string
     */
    public synchronized String checkSignature(String idInterv) {
        String str = "";
        String com = "";
        int drp1 = 0, drp2 = 0;
        PreparedStatement stmt = null;
        ResultSet cursor = null;
        try {

            String query = "SELECT COMMENTAIRE_PHOTO_PDA FROM T_PHOTOS_PDA WHERE ID_INTERVENTION='"
                    + idInterv + "'";
            //Connection connection = getConnectionObj();
            stmt = getConnectionObj().prepareStatement(query);
            cursor = stmt.executeQuery();
            while (cursor.next()) {
                com = encodeString(cursor.getBytes(1));
                if (com.equals("SIGN_USER"))
                    drp1 = 1;
                if (com.equals("SIGN_CLIENT"))
                    drp2 = 1;
            }
//            cursor.close();
//            stmt.close();
            //connection.commit();
//            connDao.commit();
            //connection.release();
        } catch (Exception e) {
            Logger.printException(e);
        } finally {
            if (cursor != null) {
                try {
                    cursor.close();
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
            if (stmt != null) {
                try {
                    stmt.close();
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
            try {
                connDao.commit();
            } catch (Exception e) {
                e.printStackTrace();
            }
        }

        if (drp1 == 0 && drp2 == 0)
            str = "SIGN";
        else if (drp1 == 1 && drp2 == 0)
            str = "SIGN_USER";
        else if (drp1 == 0 && drp2 == 1)
            str = "SIGN_CLIENT";
        else if (drp1 == 1 && drp2 == 1)
            str = "SIGN_DONE";

        return str;
    }

